INSERT INTO `business_work` VALUES (23, '2020-08-04 14:40:55', 0, 1, 0, '成华区退役军人事务局副局长邓嵘对白莲池街道办优抚体系建设检查指导', '', '<p align=\"center\" style=\"text-align:center\"><span style=\"font-size:10.5pt\"><span style=\"font-family:Calibri,sans-serif\"><b><span style=\"font-size:22.0pt\"><span style=\"font-family:宋体\">成华区退役军人事务局副局长邓嵘对白莲池街道办优抚体系建设检查指导</span></span></b></span></span></p>\r\n\r\n<p style=\"text-indent:32.0pt; text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:Calibri,sans-serif\"><span lang=\"EN-US\" style=\"font-size:16.0pt\">2019</span><span style=\"font-size:16.0pt\"><span style=\"font-family:宋体\">年</span></span><span lang=\"EN-US\" style=\"font-size:16.0pt\">12</span><span style=\"font-size:16.0pt\"><span style=\"font-family:宋体\">月</span></span><span lang=\"EN-US\" style=\"font-size:16.0pt\">13</span><span style=\"font-size:16.0pt\"><span style=\"font-family:宋体\">日上午，成华区退役军人事务局副局长邓嵘一行对白莲池街道办事处指导优抚体系建设情况。</span></span></span></span></p>\r\n\r\n<p style=\"text-indent:32.0pt; text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"font-family:宋体\">检查组，对便民大厅办事窗口、活动开展信息，优抚对象台账，工作制度等资料进行检查，听取了工作人员对优抚体系推进相关工作的介绍。</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:Calibri,sans-serif\"><span lang=\"EN-US\" style=\"font-size:16.0pt\"><img alt=\"\" src=\"http://admin.tuiyijunrenejia.cn/upload/f2/cd6c0cff2db0d2e44d1bbe060d73ac.jpg\" style=\"max-width:100%;border:0\" />&nbsp; <img alt=\"\" src=\"http://admin.tuiyijunrenejia.cn/upload/66/42ccc606a89b027ea722c644556328.jpg\" style=\"max-width:100%;border:0\" /></span></span></span></p>\r\n\r\n<p style=\"text-indent:32.0pt; text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"font-family:宋体\">检查组对白莲池街道办事处优抚体系建设工作给予了肯定，邓嵘副局长强调各社区要结合亲民化改造、党建工作、优抚体系工作，打造有特色亮点的退役军人之家，把退役军人当家人帮助解决实际困难，增强退役军人荣誉感、获得感、归属感。</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:Calibri,sans-serif\"><span lang=\"EN-US\" style=\"font-size:16.0pt\"><img alt=\"\" src=\"http://admin.tuiyijunrenejia.cn/upload/8c/a62cbe37dff570de115edf5ef27d91.jpg\" style=\"max-width:100%;border:0\" />&nbsp; <img alt=\"\" src=\"http://admin.tuiyijunrenejia.cn/upload/c6/95cfc205caaa05fb50a60ffb7885c8.jpg\" style=\"max-width:100%;border:0\" /></span></span></span></p>', '2020-08-04 15:01:03', NULL);
